DROP TABLE IF EXISTS `settings`;
CREATE TABLE IF NOT EXISTS `settings` (
  `id` int(11) NOT NULL,
  `name` varchar(100) NOT NULL,
  `value` varchar(100) NOT NULL,
  PRIMARY KEY  (`id`)
);



DROP TABLE IF EXISTS `quotes`;
CREATE TABLE IF NOT EXISTS `quotes` (
  `id` int(11) NOT NULL ,
  `author1` varchar(100) NOT NULL,
  `author2` varchar(100) NOT NULL,
  `quote` varchar(600) NOT NULL,
  `categories` varchar(100) NOT NULL,
  `dateadded` datetime NOT NULL,
  `new` tinyint(4) NOT NULL default '0',
  `views` int(11) NOT NULL default '0',
  `submitter` varchar(30) NOT NULL,
  `votesup` int(11) NOT NULL default '0',
  `votesdown` int(11) NOT NULL default '0',
  `lastviewed` datetime NOT NULL,
  `safe` tinyint(4) NOT NULL default '1',
  PRIMARY KEY  (`id`)
  ) ;


