(4238, 'Kin Hubbard', '', '"The hardest thing is to take less when you can get more."  ', '(insightful)', '2009-04-14 22:05:39', 0, 791, '', 0, 0, '0000-00-00 00:00:00', 1),
(4239, 'Ivy Baker Priest', '', '"The world is round and the place which may seem like the end may also be the beginning."', '(insightful)', '2009-04-14 22:05:39', 0, 803, '', 0, 0, '0000-00-00 00:00:00', 1),
(4240, 'James Dean', '', '"Dream as if you''ll live forever. Live as if you''ll die today."', '(insightful)', '2009-04-14 22:05:39', 0, 751, '', 0, 0, '0000-00-00 00:00:00', 1),
(4241, 'Vernon Howard', '', '"You have succeeded in life when all you really want is only what you really need." ', '(insightful)', '2009-04-14 22:05:39', 0, 754, '', 0, 0, '0000-00-00 00:00:00', 1),
(4242, 'Confucius', '', '"Our greatest glory is not in never falling, but in getting up every time we do."', '(motivation)', '2009-04-14 22:05:39', 0, 634, '', 0, 0, '0000-00-00 00:00:00', 1),
(4243, 'Confucius', '', '"It does not matter how slowly you go so long as you do not stop."', '(motivation)', '2009-04-14 22:05:39', 0, 615, '', 0, 0, '0000-00-00 00:00:00', 1),
