(4289, 'Bob', 'Lost In Translation', '"The more you know who you are and what you want, the less you let things upset you."', '(movie, insightful)', '2009-04-14 22:05:39', 0, 926, '', 0, 0, '0000-00-00 00:00:00', 1),
(4290, 'Moby', 'Slipping Away', '"Focus on everything better today."', '(insightful)', '2009-04-14 22:05:39', 0, 781, '', 0, 0, '0000-00-00 00:00:00', 1),
(4291, 'Moby', 'Slipping Away', '"Open to everything happy and sad; seeing the good when it''s all going bad."', '(insightful)', '2009-04-14 22:05:39', 0, 826, '', 0, 0, '0000-00-00 00:00:00', 1),
(4292, 'Mary Wilson Little', '', '"There is no pleasure in having nothing to do; the fun is in having lots to do and not doing it."', '(funny)', '2009-04-14 22:05:39', 0, 969, '', 0, 0, '0000-00-00 00:00:00', 1),
(4293, 'John Erskine', '', '"Music is the only language in which you cannot say a mean or sarcastic thing."', '()', '2009-04-14 22:05:39', 0, 558, '', 0, 0, '0000-00-00 00:00:00', 1),
