(4296, 'Theodore Roosevelt', '', '"When you play, play hard; when you work, don''t play at all."', '(game, insightful)', '2009-04-14 22:05:39', 0, 846, '', 0, 0, '0000-00-00 00:00:00', 1),
(4297, 'Theodore Roosevelt', '', '"It is hard to fail, but it is worse never to have tried to succeed."', '(motivation)', '2009-04-14 22:05:39', 0, 669, '', 0, 0, '0000-00-00 00:00:00', 1),
